import products from './data/products';
import ProductList from './components/ProductList.vue';
import BasePagination from './components/BasePagination.vue';
export default {
  name: 'App',
  components: {
    ProductList,
    BasePagination
  },
  data() {
    return {
      page: 1,
      productsPerPage: 9
    };
  },
  computed: {
    products() {
      return products.slice((this.page - 1) * this.productsPerPage, this.page * this.productsPerPage);
    },
    countProducts() {
      return products.length;
    }
  }
};
